import { Component } from '@angular/core';
import { ProduitService } from '../services/produit.service';
import { ActivatedRoute,Router } from '@angular/router';
import { Produit } from '../model/produit.model';
import { Categorie } from '../model/categorie.model';

@Component({
  selector: 'app-update-produit',
  templateUrl: './update-produit.component.html',
  styleUrls: []
})
export class UpdateProduitComponent {

  currentProduit = new Produit();
  categories! : Categorie[];
  updatedCatId! : number;


  constructor(private activatedRoute: ActivatedRoute,
    private router :Router,
    private produitService: ProduitService) { }


    ngOnInit() {
      this.produitService.consulterProduit(this.activatedRoute.snapshot.params['id']).
      subscribe( prod =>{ this.currentProduit = prod; } ) ;
    }

      updateProduit()
      { 
        this.produitService.updateProduit(this.currentProduit).subscribe(prod => {
          this.router.navigate(['produits']); }
          );
      }
}
<div class="container">
    <div>
        <h2>Modifier un Produit :</h2>
    </div>
    <form >
        <div class="col-sm-2 col-md-2 col-lg-2">
            <label >ID Produit</label>
            <input readonly type="text" [(ngModel)]="currentProduit.idProduit" name="idProduit" class="form-control">
        </div>
        <div class="col-sm-4 col-md-4 col-lg-4" >
            <label >Nom Produit</label>
            <input type="text"[(ngModel)]= "currentProduit.nomProduit" name="nomProduit" class="form-control">
        </div>
        <div class="col-sm-2 col-md-2 col-lg-2">
            <label >Prix Produit</label>
            <input type="number" [(ngModel)]= "currentProduit.prixProduit" name="prixProduit" class="form-control">
        </div>
        <div class="col-sm-4 col-md-4 col-lg-4">
            <label >Date création</label>
            <input type="date" [ngModel]="currentProduit.dateCreation | date: 'yyyy-MM-dd'" (ngModelChange)=" currentProduit.dateCreation= $event" name="dateCreation" class="form-control">
        </div>

        <div class="col-sm-4 col-md-4 col-lg-4">
            <label>Categorie</label>
            <select class="form-control form-control-lg" id="idCat" name="idCat" [(ngModel)]="updatedCatId">
                <option *ngFor="let cat of categories" [value]="cat.idCat">
                    {{cat.nomCat}}
                </option>
            </select>
        </div>

        <div class="mt-2">
            <button type="submit" (click)="updateProduit()" class="btn btn-success">Modifer</button>
        </div>
    </form>
</div>
